class Solution { public:     int leastBricks(vector<vector<int>>& wall) {         map<int,int> m;         int size=wall.size();         for(int i=0;i<size;++i)         {             if(wall[i].size()>1)             {                 ++m[wall[i][0]];                 for(int j=1;j<wall[i].size()-1;++j)                 {                     wall[i][j]+=wall[i][j-1];                     ++m[wall[i][j]];                 }             }         }         int max=0;         for(auto i:m)         {             if(i.second>max)max=i.second;         }         return size-max;     } };