/**  * Definition for a binary tree node.  * struct TreeNode {  *     int val;  *     TreeNode *left;  *     TreeNode *right;  *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}  * };  */ class Solution { public:      int recur(TreeNode* r,int to_add)     {         if(!r)return 0;         int res=to_add;         if(r->right)             res+=recur(r->right,res);         res+=r->val;         r->val=res;         if(r->left)             res+=recur(r->left,res);         return res-to_add;     }            TreeNode* convertBST(TreeNode* root) {         if(!root)return nullptr;         recur(root,0);         return root;              } };