/**  * Definition for a binary tree node.  * struct TreeNode {  *     int val;  *     TreeNode *left;  *     TreeNode *right;  *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}  * };  */ class Solution { public:       int recur(TreeNode* t,int& m)     {         if(!t)return 0;         int temp1=0,temp2=0;         if(t->left)             temp1=recur(t->left,m);         if(t->right)             temp2=recur(t->right,m);         if(temp1+temp2>m)m=temp1+temp2;         return 1+max(temp1,temp2);     }     int diameterOfBinaryTree(TreeNode* root) {         if(!root)return 0;         if(root && !root->left && !root->right)return 0;         int m=0;         recur(root,m);         return m;     } };